import React from 'react'
import '../style/Video.css';
import Stream from './Stream';

export default class VideoPage extends React.Component {

    constructor(props) {
        super(props);
        this.state = { joinSession:false, };
    };

    handleChange = (event) => {
        this.setState({joinSession: !joinSession});
    }

    handleSubmit = (event) => {
        alert('EC URL Submitted');

        fetch('http://localhost:3001/store-data', {
            method: 'POST',
            headers:{
                'Content-type':'application/json'
            },
            body: JSON.stringify(this.state)
            })
            .then((result) => result.json())
            .then((info) => {console.log(info); })

        event.preventDefault();
    }

    render(){
        return(
            <div className="Video">
                <h2> VIDEO </h2>
                <form onSubmit={this.handleSubmit}>
                    <input type="submit" id='joinSession' name="Join Session" onChange={this.handleChange} />
                </form>
                { joinSession==true &&
                <Stream />
                }
            </div>
        );
    };
};
