import React from 'react'
import './style/Video.css';
import Stream from './Stream';

export default class VideoPage extends React.Component {
    constructor(props) {
        super(props);


        this.state = {
          position1:false,
          position2:false,
          position3:false,
          position4:false,
        };
    };

    togglePosition1 = () => {
        this.setState(state => ({
            position1: !state.position1,
        }));
        if(position1){
            <Stream />
        }
    };


    render(){
        return(
            <div className="Video">
                <h4>VIDEO</h4>
            <button id="position1" onClick={this.togglePosition1()}> Position 1 </button>


            </div>
        );
    };
};
