import React from 'react'
import '../style/Video.css';
import Stream from './Stream';

export default class VideoPage extends React.Component {

    constructor(props) {
        super(props);
        this.state = { joinSession:false, };
    };


    toggleButton = () => {
        this.setState(({
            joinSession: !this.joinSession,
        }));
        alert("Button Change")
        alert(this.joinSession)
    };

    render(){
        return(
            <div className="Video">
                <h2> VIDEO </h2>
                <div className='videoButton'>
                <button id="sessionButton" onClick={this.toggleButton}>Join Session</button>
                </div>
                { this.joinSession === true &&
                    <Stream />
                }
            </div>
        );
    };
};
