import React from 'react';
import axios from "axios";

export default class ArchivePage extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            archives:[],
            archiveId:'',
        };
    }

    handleInputChange = e => {
        this.setState({
          [e.target.name]: e.target.value,
        });
    };

    handleSubmit = e => {
    e.preventDefault();

    const { bookID, bookTitle, bookAuthor } = this.state;

    const book = {
        bookID,
        bookTitle,
        bookAuthor,
    };

    axios
      .post('http://localhost:3001/getArchive', book)
      .then(() => console.log('Book Created'))
      .catch(err => {
        console.error(err);
      });
  };

    componentDidMount() {
        fetch('/listarchives')
          .then(response => response.json())
          .then(json => this.setState({ archives: json }));
      }


    render() {

        return(
            <div>
                <h1>Past Archives: </h1>
                <form action="http://localhost:3001/getarchive" method ="GET">
                    <h5>Get Archive: </h5>
                    <input type="archiveId" name = "archiveId" placeholder="Enter Archive ID: " id ="archiveId" required></input>
                    <input type="submit" value="Find Archive"></input>
                </form>

                <p></p>
                <form action="http://localhost:3001/deletearchive" method="POST">
                        <h5>Delete Archive:  </h5>
                        <input type="deleteArchive" name = "deleteArchive" placeholder="Enter Archive ID: " id ="deleteArchive" required></input>
                        <input type="submit" value="Delete Archive"></input>
                </form>

                {
                this.state.archives.length === 0
                    ? 'Loading users...'
                    : 
                    this.state.archives.map(archive => (
                        <table class="table table-striped">

                            <tbody>
                                <tr>
                                    <td><b>Name: </b>{archive.name || "Untitled"}</td>
                                    <td><b>Time: </b>{archive.duration} seconds</td>
                                    <td><b>Status: </b>{archive.status}</td>
                                    <td><b>Archive ID: </b> {archive.id}</td>

                                </tr>
                            </tbody>
                        </table>

                    ))
                }

            </div>
        )
    };
};
