import React from 'react'
import './style/Video.css';
import Publisher from './components/Publisher';
import { OTSession, OTStreams } from 'opentok-react';



export default class VideoPage extends React.Component {
    componentDidMount(){
        fetch("/api")
          .then((res) => res.json())
          .then((data) => this.setState({apiKey:data.apiKey, sessionId:data.sessionId, token:data.token}))
          .catch((err) => {
            console.error('Failed to get session creddentials', err);
            alert('Failed to get opentok sessionId and token');
          });

      }

    constructor(props) {
        super(props);

        this.state = {
          position1:false,
          position2:false,
          position3:false,
          position4:false,
          apiKey:"",
          sessionId:"",
          token:"",
        };
        this.componentDidMount();
    };

    togglePosition1 = () => {
        this.setState(state => ({
            position1: !state.position1,
        }));
        if(this.position1){
            alert('Stream 1');
            {this.apiKey !== "" && this.sessionId !== "" && this.token !== "" && 
            <OTSession
              apiKey={this.apiKey}
              sessionId={this.sessionId}
              token={this.token}
              onError={this.onSessionError}
              eventHandlers={this.sessionEventHandlers}
            >

              <Publisher />
            </OTSession>
            }
        }
    };

    togglePosition2 = () => {
        this.setState(state => ({
            position2: !state.position2,
        }));
        if(this.position2){
            alert('Stream 2');
            <OTSession
              apiKey={this.apiKey}
              sessionId={this.sessionId}
              token={this.token}
              onError={this.onSessionError}
              eventHandlers={this.sessionEventHandlers}
            >

              <Publisher />
            </OTSession>
        }
    };

    togglePosition3 = () => {
        this.setState(state => ({
            position3: !state.position3,

        }));
        if(this.position3){
            alert('Stream 3');
            <OTSession
              apiKey={this.apiKey}
              sessionId={this.sessionId}
              token={this.token}
              onError={this.onSessionError}
              eventHandlers={this.sessionEventHandlers}
            >

              <Publisher />
            </OTSession>
        }

    };
    togglePosition4 = () => {
        this.setState(state => ({
            position4: !state.position4,

        }));
        if(this.position4){
            alert('Stream 4');
            <OTSession
              apiKey={this.apiKey}
              sessionId={this.sessionId}
              token={this.token}
              onError={this.onSessionError}
              eventHandlers={this.sessionEventHandlers}
            >

              <Publisher />
            </OTSession>

        }

    };


    render(){
        const { apiKey, sessionId, token, publishVideo } = this.state;
        return(
            <div className="Video">
                <h4>VIDEO</h4>
            <div className='Position1'>
            <div class="dropdown">
                <button class="dropbtn">Move Position</button>
                  <div class="dropdown-content">
                  <button id="position1" onClick={this.togglePosition1}> Position 1</button>
                  <button id="position2" onClick={this.togglePosition2}> Position 2 </button>
                  <button id="position3" onClick={this.togglePosition3}> Position 3 </button>
                  <button id="position4" onClick={this.togglePosition4}> Position 4 </button>

                   </div>
                </div>
                </div>
            </div>
        );
    };
};
