import React from 'react'
import '../style/Video.css';
import Stream from './Stream';

export default class VideoPage extends React.Component {

    constructor(props) {
        super(props);
        this.state = { joinSession:false, };
    };


    handleSubmit = (event) => {
        alert('Joining Session');
        this.setState({joinSession: !this.joinSession});
    }

    render(){
        return(
            <div className="Video">
                <h2> VIDEO </h2>
                <form onSubmit={this.handleSubmit}>
                    <input type="submit" id='joinSession' value="Join Session" />
                </form>
                { this.joinSession == true &&
                    <Stream />
                }
            </div>
        );
    };
};
