import { throws } from 'assert';
import React from 'react';

export default class ControlsClass extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            archiving: true,
        };
    }



    toggleArchiving = () => {
        if(this.archiving){
            fetch('/start')
            .then(function(res){ console.log(res) })
        }
        else{
            fetch('/stop')
            .then(function(res){ console.log(res) })
        }

        this.setState(state => ({
            archiving: !state.archiving,
        }));
    }

    render() {
        return (
          <div>
            <button id="archiveButton" onClick={this.toggleArchiving}>
                {this.archiving ? 'Start' : 'Stop'} Archiving
            </button>
          </div>
        );
    }
}
