import React from 'react'
import './style/Video.css';
import Stream from './Stream';

export default class VideoPage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
          position1:true,
          position2:false,
          position3:false,
          position4:false,
        };
    };

    /*togglePosition1 = () => {
        this.setState(state => ({
            position1: !state.position1,
        }));
        if(this.position1){
            alert('Stream 1');
            <Stream />
        }
    };

    togglePosition2 = () => {
        this.setState(state => ({
            position2: !state.position2,
        }));
        if(this.position2){
            alert('Stream 2');
            <Stream />
        }
    };

    togglePosition3 = () => {
        this.setState(state => ({
            position3: !state.position3,

        }));
        if(this.position3){
            alert('Stream 3');
            <Stream />
        }

    };
    togglePosition4 = () => {
        this.setState(state => ({
            position4: !state.position4,

        }));
        if(this.position4){
            alert('Stream 4');
            <Stream />

        }

    };*/


    render(){
        return(
            <div className="Video">
                <h4>VIDEO</h4>
                <div className="Position1">

            { this.state.position1 &&
                <div className="Position1">
                    <Stream />
                </div>
            }
            { this.state.position2 &&
            <div className="Position2">
                <Stream />
            </div>
            }
            { this.state.position3 &&
            <div className="Position3">
                <Stream />
            </div>
            }
            { this.state.position4 &&
            <div className="Position4">
                <Stream />
            </div>
            }

                <div class="dropdown">
                <button class="dropbtn">Move Position</button>
                  <div class="dropdown-content">
                  <button id="position1" onClick={this.togglePosition1}> Position 1</button>
                  <button id="position2" onClick={this.togglePosition2}> Position 2 </button>
                  <button id="position3" onClick={this.togglePosition3}> Position 3 </button>
                  <button id="position4" onClick={this.togglePosition4}> Position 4 </button>

                   </div>
                </div>
                </div>

            </div>
        );
    };
};
