    render() {
        return (
          <div>
            <h4>Archiving Features</h4>
            <form action="http://localhost:3001/start" method ="POST">
                <input type="archiveName" name = "archiveName" placeholder="Enter Archive Name" id ="archiveName" required></input>
                <input type="submit" value="Start Archiving"></input>
            </form>
            <button id="stopArchive" onClick={this.StopArchiving}> Stop Archiving </button>
          </div>
        );
    }
}
