import React from 'react'
import './style/Controls.css';

toggleArchiving = () => {
    this.setState(state => ({
      publishVideo: !state.publishVideo,
    }));
  };


function ControlsPage(){
    return(
        <div className="Controls">
            <h4>CONTROLS</h4>
            <button id="Recording" onClick={this.toggleVideo}>
                  Start Recording
              </button>
        </div>
    );
}

export default ControlsPage
