import React from 'react';

export default class ControlsClass extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            publishVideo: true,
          };
    }



    toggleVideo = () => {
        this.setState(state => ({
          publishVideo: !state.publishVideo,
        }));
      };

    render() {
        return (
          <div>
            <h2>result</h2>
            <button id="videoButton" onClick={this.toggleVideo}>
                  {publishVideo ? 'Disable' : 'Enable'} Video
              </button>

          </div>
        );
    }
}
